$(document).ready(function(){
	$(".container").wtScroller({
		num_display:3,
		slide_width:220,
		slide_height:280,
		slide_margin:0,
		button_width:25,
		ctrl_height:21,
		margin:0,
		auto_scroll:true,
		delay:4000,
		scroll_speed:1000,
		easing:"",
		auto_scale:true,
		move_one:false,
		ctrl_type:"scrollbar",
		display_buttons:true,
		display_caption:true,
		mouseover_caption:false,
		caption_align:"bottom",
		caption_position:"inside",
		cont_nav:true,
		shuffle:false
	});
	var c=$(".panel");
	$(".topbit_ima,.topbit_lib,.topbit_detail").click(function(){
		$(".container").undoChanges().updateChanges();
		c.width(630);
		c.css("left",-Math.floor(c.outerWidth()/2));
	});
	$(".topbit_ima,.topbit_lib,.topbit_detail").trigger("click");
});
